SELECT pn.prefered_nurse_suburb_ID, pn.user_ID AS preferred_user_ID, pn.suburb_ID AS preferred_suburb_ID, s.suburb_ID, s.city_ID, u.user_ID, u.email_address, u.contact_no, np.nurse_ID, np.first_name AS nurse_first_name, np.last_name AS nurse_last_name, np.nurse_code, np.gender, np.dob FROM prefered_nurse_suburb pn JOIN suburb s ON pn.suburb_ID = s.suburb_ID JOIN user u ON pn.user_ID = u.user_ID JOIN nurse_profile np ON u.user_ID = np.user_ID WHERE pn.suburb_ID = 14 ORDER BY nurse_last_name ASC;